CREATE OR REPLACE FUNCTION obter_ds_sql_anzics ( nr_seq_item_p bigint) RETURNS varchar AS $body$
DECLARE

  ds_retorno_w    ANZICS_ITEM.DS_VALUES%type;


BEGIN

select	trim(both max(DS_VALUES))
into STRICT	ds_retorno_w
from	ANZICS_ITEM
where	nr_sequencia = 	nr_seq_item_p;

if (coalesce(ds_retorno_w::text, '') = '') or (position('SELECT' in upper(ds_retorno_w)) < 0) then
	ds_retorno_w	:= 'select	CD_VALUE cd , DS_VALUE ds from ANZICS_ITEM_RESULT where nr_seq_item = '||nr_seq_item_p;
end if;

RETURN ds_retorno_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
