SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_ds_status_exame ( nr_prescricao_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_status_atend_w	smallint;
ds_status_atend_w	varchar(255) := '';


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select	max(ie_status_atend)
	into STRICT	ie_status_atend_w
	from	prescr_procedimento b,
		prescr_medica a
	where	a.nr_prescricao		= b.nr_prescricao
	and	b.nr_prescricao		= nr_prescricao_p
	and	b.nr_sequencia		= nr_sequencia_p;

	select 	substr(ds_valor_dominio,0,50)
	into STRICT	ds_status_atend_w
	from 	valor_dominio
	where 	cd_dominio = 1030
	and	vl_dominio = ie_status_atend_w;

end if;

return	ds_status_atend_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
