CREATE OR REPLACE FUNCTION obter_ds_status_proj_rec ( nr_seq_proj_rec_p bigint) RETURNS varchar AS $body$
DECLARE


dt_liberacao_w 		projeto_recurso.dt_liberacao%type;
dt_fim_exec_w 		projeto_recurso.dt_fim_exec%type;
dt_suspensa_w 		projeto_recurso.dt_suspensa%type;
dt_bloqueio_w 		projeto_recurso.dt_bloqueio%type;
dt_aprovacao_w 		projeto_recurso.dt_aprovacao%type;
dt_reprovacao_w 	projeto_recurso.dt_reprovacao%type;
dt_desdobr_aprov_w 	projeto_recurso.dt_desdobr_aprov%type;
dt_cancelamento_w 	projeto_recurso.dt_cancelamento%type;
nr_seq_aprovacao_w 	projeto_recurso.nr_seq_aprovacao%type;
ie_status_w		varchar(1);
nr_seq_proj_rec_w	projeto_recurso.nr_sequencia%type;



BEGIN

begin
select 	nr_sequencia,
	dt_liberacao,
	dt_fim_exec,
	dt_suspensa,
	dt_bloqueio,
	dt_cancelamento,
	dt_aprovacao,
	dt_reprovacao,
	dt_desdobr_aprov,
	nr_seq_aprovacao
into STRICT 	nr_seq_proj_rec_w,
	dt_liberacao_w,
	dt_fim_exec_w,
	dt_suspensa_w,
	dt_bloqueio_w,
	dt_cancelamento_w,
	dt_aprovacao_w,
	dt_reprovacao_w,
	dt_desdobr_aprov_w,
	nr_seq_aprovacao_w
from    projeto_recurso
where	nr_sequencia = nr_seq_proj_rec_p;
exception
when no_data_found or too_many_rows then
	nr_seq_proj_rec_w := null;
end;

if (dt_bloqueio_w IS NOT NULL AND dt_bloqueio_w::text <> '') then
	ie_status_w := 'B';
elsif (dt_cancelamento_w IS NOT NULL AND dt_cancelamento_w::text <> '') then
	ie_status_w := 'T';
elsif (coalesce(dt_liberacao_w::text, '') = '') then
	ie_status_w := 'P';
elsif ((dt_fim_exec_w IS NOT NULL AND dt_fim_exec_w::text <> '') and trunc(dt_fim_exec_w) <= trunc(clock_timestamp())) then
	ie_status_w := 'E';
elsif (dt_suspensa_w IS NOT NULL AND dt_suspensa_w::text <> '') then
	ie_status_w := 'S';
elsif (dt_liberacao_w IS NOT NULL AND dt_liberacao_w::text <> '') and (nr_seq_aprovacao_w IS NOT NULL AND nr_seq_aprovacao_w::text <> '') and (coalesce(dt_aprovacao_w::text, '') = '') and (coalesce(dt_reprovacao_w::text, '') = '') and (coalesce(dt_desdobr_aprov_w::text, '') = '') then
	ie_status_w := 'A';
else
	ie_status_w := 'C';
end if;

return obter_descricao_dominio(8313, ie_status_w);

end;
$body$
