CREATE OR REPLACE FUNCTION obter_ds_tipo_acuidade_visual (ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255) := wheb_mensagem_pck.get_texto(308929); -- Não informado
BEGIN

if (ie_tipo_p = 'S') then
	ds_retorno_w := wheb_mensagem_pck.get_texto(363198); -- Nenhum
elsif (ie_tipo_p = 'C') then
	ds_retorno_w := wheb_mensagem_pck.get_texto(308931); -- Lente de contato
elsif (ie_tipo_p = 'O') then
	ds_retorno_w := wheb_mensagem_pck.get_texto(308932); -- Lente de óculos
elsif (ie_tipo_p = 'A') then
	ds_retorno_w := wheb_mensagem_pck.get_texto(308933); -- Ambos
end if;

return	ds_retorno_w;

end;
