CREATE OR REPLACE FUNCTION obter_ds_tipo_historico_inst ( cd_perfil_p bigint, nr_seq_pasta_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN
if (cd_perfil_p IS NOT NULL AND cd_perfil_p::text <> '' AND nr_seq_pasta_p IS NOT NULL AND nr_seq_pasta_p::text <> '') then
	select	coalesce(c.ds_pasta_instituicao,b.ds_caption_instituicao) ds_retorno
	into STRICT	ds_retorno_w
	from	perfil_item_pront d,
			perfil_item_pront_pasta c,
			prontuario_item_pasta b,
			prontuario_pasta a
	where	a.nr_sequencia = b.nr_seq_pasta
	and		b.nr_sequencia = c.nr_seq_item_pasta
	and		d.nr_sequencia = c.nr_seq_item_perfil
	and		d.cd_perfil = cd_perfil_p
	and		a.nr_sequencia	= nr_seq_pasta_p
	and		d.nr_seq_item_pront = 93;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
