CREATE OR REPLACE FUNCTION obter_ds_titulo_pagar ( nr_seq_trib_venc_w pls_pag_prest_venc_trib.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);

BEGIN
-- inicia a variável
ds_retorno_w := null;

-- se não foi passado a sequencia nem faz nada
if (nr_seq_trib_venc_w IS NOT NULL AND nr_seq_trib_venc_w::text <> '') then

	-- primeiro verifica na própria tabela o campo nr_titulo_pagar_ret (este é alimentado quando o titulo do tributo
	-- é gerado via lote de retenção)
	select 	max(nr_titulo_pagar_ret)
	into STRICT	ds_retorno_w
	from	pls_pag_prest_venc_trib
	where 	nr_sequencia = nr_seq_trib_venc_w;

	-- se não encontrou significa que é alimentado na tabela titulo_pagar o nr_sequencia%
	-- isto para os titulos de tributos que não são gerados pelo lote de retenção.
	if (coalesce(ds_retorno_w::text, '') = '') then

		select 	max(a.nr_titulo)
		into STRICT	ds_retorno_w
		from    titulo_pagar a
		where	a.nr_seq_pls_venc_trib = nr_seq_trib_venc_w;
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_ds_titulo_pagar ( nr_seq_trib_venc_w pls_pag_prest_venc_trib.nr_sequencia%type) FROM PUBLIC;

