SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_ds_triagem_mapa_html (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_classif_w	bigint;
ds_cor_w		TRIAGEM_CLASSIF_RISCO.DS_CLASSIFICACAO%type	:=	'';



BEGIN


select	max(NR_SEQ_CLASSIF)
into STRICT	nr_seq_classif_w
from	TRIAGEM_PRONTO_ATEND
where	nr_atendimento	=	nr_atendimento_p;

if (nr_seq_classif_w > 0) then
	begin

	select	max(DS_CLASSIFICACAO)
	into STRICT	ds_cor_w
	from	TRIAGEM_CLASSIF_RISCO
	where	nr_sequencia = nr_seq_classif_w;

	end;
end if;


return	ds_cor_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_ds_triagem_mapa_html (nr_atendimento_p bigint) FROM PUBLIC;

