CREATE OR REPLACE FUNCTION obter_ds_ult_evolucao_varchar_atx ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_tasy_conversao_rtf_w	tasy_conversao_rtf.nr_sequencia%type;
cd_evolucao_w			evolucao_paciente.cd_evolucao%type;
ds_retorno_w			varchar(2000);
BEGIN

select	max(cd_evolucao)
into STRICT	cd_evolucao_w
from	evolucao_paciente
where	nr_atendimento = nr_atendimento_p
and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
and	coalesce(ie_situacao,'A') = 'A';

nr_seq_tasy_conversao_rtf_w := converte_rtf_string('select ds_evolucao from evolucao_paciente where cd_evolucao = :cd_evolucao_p', to_char(cd_evolucao_w), 'Tasy', nr_seq_tasy_conversao_rtf_w);

select	substr( ds_texto_clob, 2000, 1 )
into STRICT	ds_retorno_w
from	tasy_conversao_rtf
where	nr_sequencia = nr_seq_tasy_conversao_rtf_w;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
