CREATE OR REPLACE FUNCTION obter_dt_atend_futuro (NR_SEQ_ATEND_FUTURO_P bigint) RETURNS timestamp AS $body$
DECLARE


ds_retorno_w	timestamp;


BEGIN
if (NR_SEQ_ATEND_FUTURO_P IS NOT NULL AND NR_SEQ_ATEND_FUTURO_P::text <> '') then

	select	max(a.DT_PREVISTA_ATEND_FUT)
	into STRICT	ds_retorno_w
	from	ATEND_PAC_FUTURO a,
			PACIENTE_ESPERA b
	where	b.NR_SEQ_ATEND_FUTURO	= a.NR_SEQUENCIA
	and     b.NR_SEQ_ATEND_FUTURO = NR_SEQ_ATEND_FUTURO_P;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
