CREATE OR REPLACE FUNCTION obter_dt_bloqueio_agenda_cir (nr_seq_agenda_p bigint, nr_atendimento_p bigint, cd_convenio_p bigint) RETURNS timestamp AS $body$
DECLARE

dt_retorno_w	timestamp;


BEGIN

if (coalesce(nr_seq_agenda_p,0) > 0) then
	begin
	select	coalesce(max(a.dt_bloqueio_faturamento),max(b.dt_bloqueio_faturamento))
	into STRICT	dt_retorno_w
	from	agenda_paciente a,
		cirurgia b
	where	a.nr_sequencia = b.nr_seq_agenda
	and	a.nr_sequencia = nr_seq_agenda_p;
	exception
	when others then
		dt_retorno_w := null;
	end;
elsif (coalesce(nr_atendimento_p,0) > 0) then
	begin
	select	max(dt_bloqueio_faturamento)
	into STRICT	dt_retorno_w
	from	cirurgia
	where	nr_atendimento		= nr_atendimento_p
	and	cd_convenio		= cd_convenio_p;
	exception
	when others then
		dt_retorno_w := null;
	end;
end if;


return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
