SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dt_cirurgia_paciente ( nr_atendimento_p text) RETURNS timestamp AS $body$
DECLARE

 
 
cd_pessoa_fisica_w	agenda_paciente.cd_pessoa_fisica%type;
dt_retorno_w		timestamp;
nr_sequencia_w		agenda_paciente.nr_sequencia%type;
nr_cirurgia_w		cirurgia.nr_cirurgia%type;
dt_cirurgia_w		timestamp;


BEGIN 
 
select	obter_pessoa_atendimento(nr_atendimento_p,'C') 
into STRICT	cd_pessoa_fisica_w
;
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
	 
	SELECT	MAX(nr_sequencia) 
	into STRICT	nr_sequencia_w 
	FROM	agenda_paciente a 
	WHERE	cd_pessoa_fisica	= cd_pessoa_fisica_w	 
	AND	hr_inicio > clock_timestamp() 
	AND	(cd_procedimento IS NOT NULL AND cd_procedimento::text <> '') 
	AND	ie_status_agenda NOT IN ('E','C') 
	AND	EXISTS ( SELECT 1 
		  	 FROM	agenda x 
			 WHERE a.cd_agenda 		 = x.cd_agenda	 
			 AND	x.cd_tipo_agenda 	 = 1 ); 	
 
	 
	select	max(dt_agendamento) 
	into STRICT	dt_cirurgia_w 
	from	agenda_paciente a, 
		procedimento p 
	where	a.cd_procedimento 	= p.cd_procedimento 
	and	a.ie_origem_proced 	= p.ie_origem_proced 
	and	nr_sequencia		= nr_sequencia_w;
 
	if (coalesce(dt_cirurgia_w::text, '') = '') then 
 
		SELECT	MAX(nr_cirurgia) 
		into STRICT	nr_cirurgia_w 
		FROM	cirurgia 
		WHERE	nr_atendimento		= nr_atendimento_p 
		AND	ie_status_cirurgia 	= 2;
		 
		if (nr_cirurgia_w IS NOT NULL AND nr_cirurgia_w::text <> '') then 
			SELECT	max(coalesce(a.dt_inicio_real, a.dt_inicio_prevista)) 
			into STRICT	dt_cirurgia_w 
			FROM	cirurgia a, 
				procedimento p 
			WHERE	p.cd_procedimento 	= a.cd_procedimento_princ 
			AND	p.ie_origem_proced 	= a.ie_origem_proced 	 
			AND	nr_cirurgia		= nr_cirurgia_w;
		end if;
 
	end if;
	dt_retorno_w	:= dt_cirurgia_w;
end if;
 
return	dt_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dt_cirurgia_paciente ( nr_atendimento_p text) FROM PUBLIC;

