CREATE OR REPLACE FUNCTION obter_dt_credito_banco_cre (nr_titulo_p bigint, nr_seq_baixa_p bigint) RETURNS timestamp AS $body$
DECLARE


ds_retorno_w		timestamp;
dt_credito_banco_w	timestamp;


BEGIN

if ( coalesce(nr_titulo_p,0) <> 0 ) and ( coalesce(nr_seq_baixa_p,0) <> 0 ) then

	select	max(a.dt_credito_banco)
	into STRICT	dt_credito_banco_w
	from	titulo_receber_liq a
	where	a.nr_titulo		= nr_titulo_p
	and		a.nr_sequencia	= nr_seq_baixa_p;

end if;

ds_retorno_w := dt_credito_banco_w;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
