CREATE OR REPLACE FUNCTION obter_dt_diag_atend_js (nr_atendimento_p bigint) RETURNS timestamp AS $body$
DECLARE

 
dt_diagnostico_w	timestamp;	
dt_alta_w		timestamp;	
qt_diagnostico_w	bigint;	
			 

BEGIN 
 
if (coalesce(nr_atendimento_p,0) > 0) then 
 
	select	dt_alta dt_alta 
	into STRICT	dt_alta_w 
	from 	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p;
	 
	select count(*) 
	into STRICT	qt_diagnostico_w 
	from 	diagnostico_medico  
	where 	nr_atendimento = nr_atendimento_p;
	 
	if (dt_alta_w IS NOT NULL AND dt_alta_w::text <> '') then 
		dt_diagnostico_w := dt_alta_w - coalesce(qt_diagnostico_w,0) /86400;
	else 
		dt_diagnostico_w := clock_timestamp();
	end if;
	 
 
end if;
 
return	dt_diagnostico_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
