CREATE OR REPLACE FUNCTION obter_dt_emissao_nf ( nr_interno_conta_p bigint) RETURNS timestamp AS $body$
DECLARE

 
dt_emissao_nf_w		timestamp := null;
nr_seq_protocolo_w	bigint := null;
nr_seq_nota_fiscal_w	bigint := null;
nr_seq_lote_protocolo_w	bigint := null;


BEGIN 
 
select	max(dt_emissao) 
into STRICT	dt_emissao_nf_w 
from	nota_fiscal 
where	nr_interno_conta = nr_interno_conta_p;
 
if (coalesce(dt_emissao_nf_w::text, '') = '') then 
	 
	select	max(nr_seq_protocolo) 
	into STRICT	nr_seq_protocolo_w 
	from	conta_paciente 
	where	nr_interno_conta	= nr_interno_conta_p;
 
	if (nr_seq_protocolo_w IS NOT NULL AND nr_seq_protocolo_w::text <> '') then 
		select	max(dt_emissao) 
		into STRICT	dt_emissao_nf_w 
		from	nota_fiscal 
		where	nr_seq_protocolo	= nr_seq_protocolo_w;
 
		if (coalesce(dt_emissao_nf_w::text, '') = '') then 
			select	max(nr_seq_nf_saida) 
			into STRICT	nr_seq_nota_fiscal_w 
			from	titulo_receber 
			where	nr_seq_protocolo	= nr_seq_protocolo_w;
 
			if (coalesce(nr_seq_nota_fiscal_w::text, '') = '') then 
				select	max(nr_seq_lote_protocolo) 
				into STRICT	nr_seq_lote_protocolo_w 
				from	protocolo_convenio 
				where	nr_seq_protocolo	= nr_seq_protocolo_w;
			 
				if (nr_seq_lote_protocolo_w IS NOT NULL AND nr_seq_lote_protocolo_w::text <> '') then 
					select	max(dt_emissao) 
					into STRICT	dt_emissao_nf_w 
					from	nota_fiscal 
					where	nr_seq_lote_prot	= nr_seq_lote_protocolo_w;
				end if;
 
				if (coalesce(dt_emissao_nf_w::text, '') = '') then 
					select	max(dt_emissao) 
					into STRICT	dt_emissao_nf_w 
					from	nota_fiscal a, 
						conta_paciente b 
					where	a.nr_interno_conta		= b.nr_interno_conta 
					and	a.ie_situacao		= '1' 
					and	b.nr_seq_protocolo	= nr_seq_protocolo_w;
				end if;
 
			else 
				select	max(dt_emissao) 
				into STRICT	dt_emissao_nf_w 
				from	nota_fiscal 
				where	nr_sequencia = nr_seq_nota_fiscal_w;
			end if;
			 
		end if;
 
		 
	else 
		select	max(nr_seq_nf_saida) 
		into STRICT	nr_seq_nota_fiscal_w 
		from	titulo_receber 
		where	nr_interno_conta	= nr_interno_conta_p;
 
		if (nr_seq_nota_fiscal_w IS NOT NULL AND nr_seq_nota_fiscal_w::text <> '') then 
			select	max(dt_emissao) 
			into STRICT	dt_emissao_nf_w 
			from	nota_fiscal 
			where	nr_sequencia	= nr_seq_nota_fiscal_w;
		end if;
	end if;
end if;
 
return	dt_emissao_nf_w;
 
end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
