CREATE OR REPLACE FUNCTION obter_dt_entrada_nf_item_oc ( nr_ordem_compra_p bigint, nr_item_oci_p bigint, dt_prevista_entrega_p timestamp) RETURNS timestamp AS $body$
DECLARE


dt_entrada_saida_w		timestamp;


BEGIN

select	max(a.dt_entrada_saida)
into STRICT	dt_entrada_saida_w
from	nota_fiscal a,
	nota_fiscal_item b
where	a.nr_sequencia = b.nr_sequencia
and	b.nr_ordem_compra = nr_ordem_compra_p
and	b.nr_item_oci = nr_item_oci_p
and	trunc(dt_entrega_ordem,'dd') = trunc(dt_prevista_entrega_p,'dd')
and	a.ie_situacao = '1';

return	dt_entrada_saida_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
