CREATE OR REPLACE FUNCTION obter_dt_entrada_saida_unid (nr_atendimento_p bigint, nr_seq_interno_p bigint, ie_opcao_p text) RETURNS timestamp AS $body$
DECLARE

dt_entrada_unid_w	timestamp;
dt_saida_unid_w		timestamp;
dt_referencia_w		timestamp;
--relatÃ³rio 1517
BEGIN

if (coalesce(nr_atendimento_p,0) > 0) and (coalesce(nr_seq_interno_p,0) > 0) then

	select	max(dt_entrada_unidade),
		max(dt_saida_unidade)
	into STRICT	dt_entrada_unid_w,
		dt_saida_unid_w
	from	atend_paciente_unidade
	where	nr_atendimento = nr_atendimento_p
	and	nr_seq_interno = nr_seq_interno_p;

	if (ie_opcao_p = 'E') then

		dt_referencia_w := dt_entrada_unid_w;

	elsif (ie_opcao_p = 'S') then

		dt_referencia_w := dt_saida_unid_w;
	end if;
end if;

return	dt_referencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
