CREATE OR REPLACE FUNCTION obter_dt_entrega_norma_compra ( nr_seq_norma_p bigint, cd_estabelecimento_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_entrega_w		timestamp;
dt_referencia_w		timestamp;
qt_dias_w		integer;
ie_dias_uteis_w		varchar(1);


BEGIN

dt_referencia_w		:= trunc(clock_timestamp(),'dd');

select	qt_dias,
	ie_dias_uteis
into STRICT	qt_dias_w,
	ie_dias_uteis_w
from	sup_normas_compras
where	nr_sequencia = nr_seq_norma_p;

if (ie_dias_uteis_w = 'S') then
	dt_entrega_w	:= obter_data_dias_uteis(cd_estabelecimento_p, dt_referencia_w, qt_dias_w);
else
	dt_entrega_w	:= dt_referencia_w + qt_dias_w;
end if;

return	dt_entrega_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
