SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dt_escala_dialise ( nr_seq_escala_dialise_p bigint, ie_opcao_p text) RETURNS timestamp AS $body$
DECLARE


/*
ie_opcao_p
I = data in√≠cio
F = data final
*/
dt_retorno_w		timestamp;


BEGIN

if (ie_opcao_p = 'I') then

	select	max(dt_inicio)
	into STRICT	dt_retorno_w
	from	hd_escala_dialise
	where	nr_sequencia 	= nr_seq_escala_dialise_p;

elsif (ie_opcao_p = 'F') then

	select	max(dt_fim)
	into STRICT	dt_retorno_w
	from	hd_escala_dialise
	where	nr_sequencia 	= nr_seq_escala_dialise_p;

end if;


return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
