CREATE OR REPLACE FUNCTION obter_dt_execucao_exames (nr_laudo_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w varchar(50);


BEGIN 
	if (nr_laudo_p IS NOT NULL AND nr_laudo_p::text <> '') then  
 
		select 	to_char(max(proc_pac.dt_procedimento),'dd/mm/yyyy') 
		into STRICT  ds_retorno_w 
		FROM prescr_procedimento prescr_proc
LEFT OUTER JOIN procedimento_paciente proc_pac ON (prescr_proc.nr_prescricao = proc_pac.nr_prescricao AND prescr_proc.nr_sequencia = proc_pac.nr_sequencia_prescricao)
WHERE proc_pac.nr_laudo = nr_laudo_p;
	end if;	
 
  
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
