CREATE OR REPLACE FUNCTION obter_dt_fim_real_fase_port ( nr_seq_portifolio_p bigint, nr_seq_area_fase_p bigint ) RETURNS timestamp AS $body$
DECLARE

/* ++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:  Obter data fim real de uma fase de um portifolio
---------------------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [ ] Portal [ ]  Relatórios [ ] Outros:
 --------------------------------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
+++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
dt_retorno_w timestamp := null;


BEGIN

  select  t.*
  into STRICT    dt_retorno_w
  from ( SELECT    e1.dt_fim_real
            from      proj_portifolio pf1,
                      proj_programa pr1,
                      proj_projeto p1,
                      proj_cronograma c1,
                      proj_cron_etapa e1,
                      proj_area_gerencia_fase a1,
                      proj_fase f1
            where     pf1.nr_sequencia     = pr1.nr_seq_portifolio
            and       pr1.nr_sequencia     = p1.nr_seq_programa
            and       p1.nr_sequencia      = c1.nr_seq_proj
            and       c1.nr_sequencia      = e1.nr_seq_cronograma
            and       a1.nr_sequencia      = e1.nr_seq_area_fase
            and       f1.nr_sequencia      = a1.nr_seq_fase
            and       c1.ie_situacao       = 'A'
            and       e1.ie_fase           = 'N'
            and       pf1.nr_sequencia     = nr_seq_portifolio_p
            and       e1.nr_seq_area_fase  = nr_seq_area_fase_p
            order by  e1.dt_fim_prev desc
          ) t LIMIT 1;

  return dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
