CREATE OR REPLACE FUNCTION obter_dt_fim_transf_recorr (dt_inicio_p timestamp, ie_forma_geracao_p text, ie_final_semana_p text, ds_dia_semana_p text, nr_seq_ageint_p bigint, nr_seq_ageint_item_p bigint) RETURNS timestamp AS $body$
DECLARE


qt_total_recorr_w	bigint;
dt_retorno_w		timestamp;


BEGIN

select (select	count(*)
from	agenda_consulta a,
	agenda_integrada_item b
where	a.nr_sequencia = b.nr_seq_agenda_cons
and	b.nr_seq_agenda_int = nr_seq_ageint_p
and	a.ie_status_agenda <> 'C'
--and	a.dt_agenda >= dt_agenda_w
and	b.nr_sequencia <> nr_seq_ageint_item_p
and (b.nr_sequencia > nr_seq_ageint_item_p ))
+
(select	count(*)
from	agenda_consulta a,
	agenda_integrada_item b
where	a.nr_sequencia = b.nr_seq_Agenda_cons
and	b.nr_seq_agenda_int = nr_seq_ageint_p
and	a.ie_status_agenda <> 'C'
and	b.nr_sequencia = nr_seq_ageint_item_p )
into STRICT qt_total_recorr_w
;

dt_retorno_w := Ageint_Calcular_Recorr(dt_inicio_p,
					qt_total_recorr_w,
					ie_forma_geracao_p,
					ie_final_semana_p,
					ds_dia_semana_p	);


return	dt_retorno_w;

end;
