CREATE OR REPLACE FUNCTION obter_dt_fim_versao ( dt_entrega_prev_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);


BEGIN

select	max(to_char(d.dt_fim, 'dd/mm hh24:mi') )
into STRICT	ds_retorno_w
from	escala_diaria d
where  d.dt_inicio = dt_entrega_prev_p;

RETURN	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
