CREATE OR REPLACE FUNCTION obter_dt_horarios_medicamentos ( nr_prescricoes_p text, cd_material_p bigint, contador_p bigint, dt_horario_p text, ie_opcao_p text, dt_inicial_p timestamp, dt_final_p timestamp, nr_sequencia_p bigint, nr_prescricao_p bigint, qt_dose_p bigint, cd_intervalo_p text, ie_acm_p text, ie_se_necessario_p text) RETURNS varchar AS $body$
DECLARE



dt_horario_w		varchar(255);
dt_horario_ww		timestamp;
dt_min_horario_w  		timestamp;
ds_horarios_w		varchar(255);
ds_horarios_ww		varchar(255);
dt_horario2_ww		varchar(255);
nr_prescricao_w		bigint;
nr_atendimento_w		bigint;
nr_prescricao_ww		varchar(255);
ie_acm_w		varchar(1);
ie_se_necessario_w	varchar(1);
ie_administrar_w		varchar(1);


C01 CURSOR FOR
	SELECT	distinct to_char(dt_horario,'hh24:mi') dt_horario,
		coalesce(c.ie_administrar,'S')  ie_adm,
		coalesce(c.ie_acm,'N') ie_acm,
		coalesce(c.ie_se_necessario,'N') ie_se_necessario
	from 	prescr_mat_hor a,
		prescr_medica b,
		prescr_material c
	where 	a.nr_prescricao 	= b.nr_prescricao
	and 	c.nr_prescricao 	= a.nr_prescricao
	and 	c.nr_prescricao 	= b.nr_prescricao
	and 	obter_se_contido(a.nr_prescricao,nr_prescricao_ww) = 'S'
	and	b.nr_atendimento  	= nr_atendimento_w
	and	c.qt_dose		= qt_dose_p
	and 	c.ie_agrupador	= 1
	and	a.nr_seq_material 	= c.nr_sequencia
	and	c.cd_intervalo	= cd_intervalo_p
	and	c.cd_material 	= cd_material_p
	and	a.nr_seq_material	= nr_sequencia_p
	and	b.dt_inicio_prescr between dt_inicial_p and dt_final_p
	and	to_char(dt_horario,'dd/mm/yyyy') = to_date(dt_horario_p,'dd/mm/yyyy')
	order by dt_horario;


C02 CURSOR FOR
	SELECT distinct b.nr_prescricao nr_prescricao
	from 	prescr_mat_hor a,
		prescr_medica b,
		prescr_material c
	where 	a.nr_prescricao	  = b.nr_prescricao
	and 	c.nr_prescricao  	  = a.nr_prescricao
	and 	c.nr_prescricao   	  = b.nr_prescricao
	and	b.nr_atendimento  	  = nr_atendimento_w
	and 	c.ie_agrupador   	  = 1
	and	a.nr_seq_material 	  = c.nr_sequencia
	and	a.nr_seq_material	  = nr_sequencia_p
	and	c.qt_dose		  = qt_dose_p
	and	c.cd_material 	  = cd_material_p
	and	b.dt_inicio_prescr between dt_inicial_p and dt_final_p
	order by nr_prescricao;


BEGIN

nr_atendimento_w := obter_atendimento_prescr(nr_prescricao_p);


open C02;
loop
fetch C02 into
	nr_prescricao_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin
	nr_prescricao_ww := nr_prescricao_ww || nr_prescricao_w ||',';
	end;
end loop;
close C02;


select distinct min(to_char(a.dt_horario,'dd/mm/yyyy')) dt_min_horario
into STRICT	dt_min_horario_w
from	prescr_mat_hor a,
	prescr_medica b,
	prescr_material c
where 	a.nr_prescricao   = b.nr_prescricao
and	c.nr_prescricao   = b.nr_prescricao
and	a.nr_prescricao   = c.nr_prescricao
and obter_se_contido(a.nr_prescricao,nr_prescricao_ww) = 'S'
and	b.nr_atendimento  = nr_atendimento_w
and	c.qt_dose		  = qt_dose_p
and	c.ie_agrupador    	  = 1
and	a.nr_seq_material 	  = c.nr_sequencia
--and	c.cd_material     	  = cd_material_p
and	b.dt_inicio_prescr between dt_inicial_p and dt_final_p
order by a.dt_horario;


dt_horario_ww := to_char(dt_min_horario_w + contador_p,'dd/mm/yyyy');

if (dt_horario_ww between trunc(dt_inicial_p) and trunc(dt_final_p)) then
	dt_horario2_ww	:= to_char(dt_horario_ww,'dd/mm/yyyy');
end if;

if (ie_opcao_p = 'H') and (dt_horario_p IS NOT NULL AND dt_horario_p::text <> '') then


	open C01;
	loop
	fetch C01 into
		ds_horarios_ww,
		ie_administrar_w,
		ie_acm_w,
		ie_se_necessario_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (substr(ds_horarios_ww,4,5) = '00') then
			ds_horarios_ww := substr(ds_horarios_ww,1,2);
		else
			ds_horarios_ww := ds_horarios_ww;
		end if;

		ds_horarios_w := ds_horarios_w || ' '||  ds_horarios_ww;

		if (ie_administrar_w <> 'S') then
			ds_horarios_w := '';
		end if;

		end;
	end loop;
	close C01;
end if;

if (ie_acm_w = 'S') then
	ds_horarios_w	:= 'ACM';
elsif (ie_se_necessario_w = 'S') then
	ds_horarios_w	:= 'SN';
end if;

if (ie_opcao_p = 'D') then
	dt_horario_w := dt_horario2_ww;
elsif (ie_opcao_p = 'H') then
	dt_horario_w := ds_horarios_w;
end if;


return	dt_horario_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
