CREATE OR REPLACE FUNCTION obter_dt_inicio_oncologia_md ( dt_inicio_p timestamp, nr_dia_inicial_p bigint, nr_tpo_ret_p bigint ) RETURNS timestamp AS $body$
BEGIN
    IF ( nr_tpo_ret_p = 1 ) THEN
        RETURN dt_inicio_p - ( coalesce(nr_dia_inicial_p,0) - 1 );
    END IF;

    IF ( nr_tpo_ret_p = 2 ) THEN
        RETURN dt_inicio_p - coalesce(nr_dia_inicial_p,0);
    END IF;

    IF ( nr_tpo_ret_p = 3 ) THEN
        RETURN dt_inicio_p - 1;
    END IF;
	
	RETURN NULL;

END;
