SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dt_inicio_sol_prescr2 ( nr_prescricao_p bigint, nr_seq_solucao_p bigint, hr_prim_hr_solucao_p text default null) RETURNS timestamp AS $body$
DECLARE


dt_inicio_prescr_w			timestamp;
hr_prim_hr_solucao_w		varchar(5);
dt_concat_w					timestamp;
dt_retorno_w				timestamp;
dt_primeiro_horario_w		timestamp;


BEGIN

select	max(dt_inicio_prescr),
		max(dt_primeiro_horario)
into STRICT	dt_inicio_prescr_w,
		dt_primeiro_horario_w
from	prescr_medica
where	nr_prescricao = nr_prescricao_p;
if (coalesce(hr_prim_hr_solucao_p::text, '') = '') then
	select	max(hr_prim_horario)
	into STRICT	hr_prim_hr_solucao_w
	from	prescr_solucao
	where	nr_prescricao = nr_prescricao_p
	and		nr_seq_solucao = nr_seq_solucao_p;
else
	hr_prim_hr_solucao_w := hr_prim_hr_solucao_p;
end if;

dt_concat_w := to_date(to_char(dt_inicio_prescr_w, 'DD/MM/YYYY') || ' ' || coalesce(hr_prim_hr_solucao_w, to_char(dt_primeiro_horario_w, 'hh24:mi')),  'DD/MM/YYYY HH24:MI');

if ( dt_concat_w >= dt_inicio_prescr_w ) then
	dt_retorno_w := trunc(dt_inicio_prescr_w);
else
	dt_retorno_w := trunc(dt_inicio_prescr_w) + 1;
end if;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
