SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dt_ini_patient_journey () RETURNS timestamp AS $body$
DECLARE


dt_retorno_w  timestamp;
cd_medico_w   PESSOA_FISICA.CD_PESSOA_FISICA%TYPE;
qt_meses_w    PATIENT_JOURNEY_CONFIG.QT_MESES_BUSCA%TYPE;
			

BEGIN
  select max(cd_pessoa_fisica)
  into STRICT cd_medico_w
  from usuario
  where nm_usuario = wheb_usuario_pck.get_nm_usuario;

  select max(QT_MESES_BUSCA)
  into STRICT qt_meses_w
  from PATIENT_JOURNEY_CONFIG c
  where c.ie_situacao = 'A'
    and coalesce(c.CD_ESTABELECIMENTO, wheb_usuario_pck.get_cd_estabelecimento) = wheb_usuario_pck.get_cd_estabelecimento
    and coalesce(c.cd_setor_atendimento, wheb_usuario_pck.get_cd_setor_atendimento) = wheb_usuario_pck.get_cd_setor_atendimento
    and coalesce(c.CD_PERFIL, wheb_usuario_pck.get_cd_perfil) = wheb_usuario_pck.get_cd_perfil
    and (coalesce(c.CD_ESPECIALIDADE::text, '') = '' or obter_se_especialidades_medico(cd_medico_w, c.CD_ESPECIALIDADE) = 'S');

  if (coalesce(qt_meses_w::text, '') = '') then
    dt_retorno_w := clock_timestamp() - interval '15 days';
  else
    dt_retorno_w := add_months(clock_timestamp(), (qt_meses_w * -1));
  end if;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
