CREATE OR REPLACE FUNCTION obter_dt_lib_acesso ( nr_Atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(3)	:= 'S';
qt_visita_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_visita_w
from	atendimento_visita
where	nr_atendimento		= nr_atendimento_p
and	(dt_liberacao_acesso IS NOT NULL AND dt_liberacao_acesso::text <> '')
and	ie_paciente		= 'S';

if (qt_visita_w	> 0) then
	ds_retorno_w	:= 'N';
end if;

return	ds_retorno_w;

end;
