CREATE OR REPLACE FUNCTION obter_dt_lib_reabilitacao (cd_pessoa_fisica_p text) RETURNS timestamp AS $body$
DECLARE

data_w	timestamp;

BEGIN
select max(a.dt_liberacao) dt_liberacao 
into STRICT data_w 
from med_avaliacao_paciente a 
where	a.cd_pessoa_fisica = cd_pessoa_fisica_p 
and exists (SELECT 1 
	from med_tipo_avaliacao x 
	where x.nr_sequencia = a.nr_seq_tipo_avaliacao 
	and ((x.cd_funcao = 9091) or (coalesce(x.ie_lista_espera_reab, 'N') = 'S')));
 
return	data_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
