SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dt_liq_arq_titulo (nr_titulo_p bigint, nr_seq_baixa_p bigint, nr_seq_cobranca_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_retorno_w			timestamp;
ie_dt_baixa_cobr_escrit_w	varchar(1);
cd_estabelecimento_w		smallint;


BEGIN

if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') and (nr_seq_baixa_p IS NOT NULL AND nr_seq_baixa_p::text <> '') and (nr_seq_cobranca_p IS NOT NULL AND nr_seq_cobranca_p::text <> '') then
	select	a.cd_estabelecimento
	into STRICT	cd_estabelecimento_w
	from	titulo_receber a
	where	a.nr_titulo = nr_titulo_p;

	select	coalesce(a.ie_dt_baixa_cobr_escrit,'A')
	into STRICT	ie_dt_baixa_cobr_escrit_w
	from	parametro_contas_receber a
	where	a.cd_estabelecimento	= cd_estabelecimento_w;

	/* Data liquidação no arquivo */

	if (ie_dt_baixa_cobr_escrit_w = 'A') then
		select	max(x.dt_recebimento)
		into STRICT	dt_retorno_w
		from	cobranca_escritural 	x,
			titulo_receber_liq	w
		where	x.nr_sequencia 	= w.nr_seq_cobranca
		and	w.nr_titulo	= nr_titulo_p
		and	w.nr_sequencia	= nr_seq_baixa_p;
	else
	/* Data de baixa do lote */

		select	max(x.dt_liquidacao)
		into STRICT	dt_retorno_w
		from	titulo_receber_cobr	x,
			titulo_receber_liq	a
		where	a.nr_titulo	  	= x.nr_titulo
		and	x.nr_seq_cobranca 	= a.nr_seq_cobranca
		and	a.nr_titulo		= nr_titulo_p
		and	a.nr_sequencia		= nr_seq_baixa_p;

	end if;
end if;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dt_liq_arq_titulo (nr_titulo_p bigint, nr_seq_baixa_p bigint, nr_seq_cobranca_p bigint) FROM PUBLIC;

