SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dt_movimentacao_carta (nr_sequencia_p carta_medica.nr_sequencia%type) RETURNS timestamp AS $body$
DECLARE


data_movimentacao_w timestamp;

BEGIN

select 	max(movimentacao.data) data
into STRICT 	data_movimentacao_w
from (
	SELECT	cm.nr_sequencia,
		mm.ie_modelo_cirurgia, 
		cm.nr_atendimento,
		ap.dt_alta as data 
	from 	atendimento_paciente ap,
		carta_medica cm,        
		carta_medica_modelo mm		        
	where 	ap.nr_atendimento = cm.nr_atendimento
	and 	cm.nr_seq_modelo  = mm.nr_sequencia
	and	mm.ie_modelo_cirurgia in ('N', 'T') -- Alta e Transferencia
	
UNION
  
	SELECT 	cm.nr_sequencia,
		mm.ie_modelo_cirurgia, 
		cm.nr_atendimento,
		(select max(x.dt_entrada_unidade) 
		 from 	atend_paciente_unidade x 
		 where 	nr_atendimento = cm.nr_atendimento) as data
	from 	atendimento_paciente ap,
		carta_medica cm,
		carta_medica_modelo  mm    
	where  	ap.nr_atendimento = cm.nr_atendimento
	and 	cm.nr_seq_modelo  = mm.nr_sequencia
	and	mm.ie_modelo_cirurgia in ('I') -- Transferencia interna
) movimentacao
where	movimentacao.nr_sequencia = nr_sequencia_p;

return	data_movimentacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
