CREATE OR REPLACE FUNCTION obter_dt_nasc_pac_senhas (nr_sequencia_p bigint) RETURNS timestamp AS $body$
DECLARE


ds_retorno_w			timestamp;
dt_nascimento_w			pessoa_fisica.dt_nascimento%type;


BEGIN

if (coalesce(nr_sequencia_p,0) > 0) then

	select	max(b.dt_nascimento)
	into STRICT	dt_nascimento_w
	from	paciente_senha_fila a,
			pessoa_fisica b
	where	a.nr_sequencia 		= nr_sequencia_p
	and		b.cd_pessoa_fisica 	= a.cd_pessoa_fisica;

end if;

ds_retorno_w := dt_nascimento_w;

return	ds_retorno_w;

end;
