SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dt_origem_problema (nr_sequencia_p bigint, ie_opcao_p text) RETURNS timestamp AS $body$
DECLARE




/*
    IE_OPCAO_P
    O Origem;
    R Registro.
		*/
dt_retorno_w   timestamp;


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	if (ie_opcao_p = 'O') then
		select min(dt_atualizacao_nrec)
		into STRICT	dt_retorno_w
		from lista_problema_pac
		where nr_seq_problema_sup = nr_sequencia_p;
	elsif (ie_opcao_p = 'R') then
		select dt_atualizacao_nrec
		into STRICT 	dt_retorno_w
		from lista_problema_pac
		where nr_sequencia = nr_sequencia_p;
	end if;

end if;

return  dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
