SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dt_prescr_mat_hor ( nr_sequencia_p can_ordem_prod.nr_sequencia%type) RETURNS timestamp AS $body$
DECLARE

dt_horario_w	timestamp;
qt_hours_w	bigint;
cd_material_w	material.cd_material%type;


BEGIN
	if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	begin	
		select count(hor.dt_horario)
		into STRICT qt_hours_w
		from can_ordem_item_prescr item
		inner join prescr_mat_hor hor on item.nr_seq_mat_hor = hor.nr_sequencia
		inner join can_ordem_prod ordem on item.nr_seq_ordem = ordem.nr_sequencia
		inner join prescr_material on hor.nr_seq_material = prescr_material.nr_sequencia and prescr_material.nr_prescricao = item.nr_prescricao
		where coalesce(prescr_material.nr_sequencia_diluicao::text, '') = ''
		and coalesce(item.nr_sequencia_diluente::text, '') = ''
		and ordem.ie_cancelada = 'N'
		and coalesce(hor.dt_suspensao::text, '') = ''
		and ordem.nr_sequencia =  nr_sequencia_p;
		
		select obter_material_ordem_prod(nr_sequencia,'C')
		into STRICT cd_material_w
		from can_ordem_prod
		where nr_sequencia = nr_sequencia_p;

		select hor.dt_horario
		into STRICT dt_horario_w
		from can_ordem_item_prescr item
		inner join prescr_mat_hor hor on item.nr_seq_mat_hor = hor.nr_sequencia
		inner join can_ordem_prod ordem on item.nr_seq_ordem = ordem.nr_sequencia
		inner join prescr_material on hor.nr_seq_material = prescr_material.nr_sequencia and prescr_material.nr_prescricao = item.nr_prescricao
		where coalesce(prescr_material.nr_sequencia_diluicao::text, '') = ''
		and coalesce(item.nr_sequencia_diluente::text, '') = ''
		and ordem.ie_cancelada = 'N'
		and coalesce(hor.dt_suspensao::text, '') = ''
		and ordem.nr_sequencia = nr_sequencia_p
		and ((qt_hours_w > 1 and item.cd_material = cd_material_w) or qt_hours_w = 1);

	exception
		when others then
		dt_horario_w := null;
	end;
	end if;

return dt_horario_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
