CREATE OR REPLACE FUNCTION obter_dt_prevista_exames (nr_prescricao_p bigint, nr_seq_exame_p bigint, cd_material_exame_p text, dt_prev_execucao_p timestamp) RETURNS varchar AS $body$
DECLARE

dt_prevista_w	varchar(50);
qt_itens_w	bigint;


BEGIN

select 	count(*)
into STRICT	qt_itens_w
from	prescr_procedimento
where	nr_prescricao 	= nr_prescricao_p
and	nr_seq_exame	= nr_seq_exame_p;

dt_prevista_w	:= null;

if (qt_itens_w > 1) then
	dt_prevista_w	:= to_char(dt_prev_execucao_p,'dd/mm/yyyy hh24:mi:ss');
end if;

return	dt_prevista_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
