CREATE OR REPLACE FUNCTION obter_dt_prev_oci_inspecao ( nr_sequencia_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_prevista_entrega_w		timestamp;
nr_seq_entrega_w			bigint;
nr_ordem_compra_w		bigint;

	

BEGIN

select	coalesce(max(nr_seq_entrega),0),
	max(nr_ordem_compra)
into STRICT	nr_seq_entrega_w,
	nr_ordem_compra_w
from	inspecao_recebimento
where	nr_sequencia = nr_sequencia_p;

if (nr_seq_entrega_w > 0) then
	select	ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_prevista_entrega)
	into STRICT	dt_prevista_entrega_w
	from	ordem_compra_item_entrega
	where	nr_sequencia = nr_seq_entrega_w;
else
	select	ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_entrega)
	into STRICT	dt_prevista_entrega_w
	from	ordem_compra
	where	nr_ordem_compra = nr_ordem_compra_w;
end if;

return	dt_prevista_entrega_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
