CREATE OR REPLACE FUNCTION obter_dt_prev_retirada_cobr ( nr_seq_cobranca_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_retirada_w		timestamp;
nr_seq_orgao_w		bigint;
dt_inclusao_w		timestamp;
qt_mes_retirada_w	bigint;


BEGIN

dt_retirada_w	:= null;

select	coalesce(c.dt_vencimento,coalesce(d.dt_vencimento,a.dt_inclusao)),
	NR_SEQ_ORGAO
into STRICT	dt_inclusao_w,
	nr_seq_orgao_w
FROM cobranca_orgao a, cobranca b
LEFT OUTER JOIN titulo_receber c ON (b.nr_titulo = c.nr_titulo)
LEFT OUTER JOIN cheque_cr d ON (b.nr_seq_cheque = d.nr_seq_cheque)
WHERE a.nr_seq_cobranca = b.nr_sequencia   and a.nr_sequencia	= nr_seq_cobranca_p;


select  coalesce(qt_mes_retirada,0)
into STRICT    qt_mes_retirada_w
from    orgao_cobranca
where   nr_sequencia	= nr_seq_orgao_w;


if (qt_mes_retirada_w <> 0) then
	dt_retirada_w	:= pkg_date_utils.add_month(dt_inclusaO_w, qt_mes_retirada_w,0);
end if;

return dt_retirada_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
