CREATE OR REPLACE FUNCTION obter_dt_primeiro_item_conta (nr_atendimento_p bigint) RETURNS timestamp AS $body$
DECLARE

				 
dt_item_w				timestamp;
dt_entrada_w			timestamp;
nr_seq_classificacao_w	atendimento_paciente.nr_seq_classificacao%type;


BEGIN 
 
begin 
	select	nr_seq_classificacao, 
			dt_entrada 
	into STRICT	nr_seq_classificacao_w, 
			dt_entrada_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p  LIMIT 1;
 
exception 
when others then 
	nr_seq_classificacao_w := 0;
end;
 
if	coalesce(nr_seq_classificacao_w,0) in (8,12) then 
 
	select	min(dt_item) 
	into STRICT	dt_item_w 
	from (SELECT	min(dt_atendimento) dt_item 
		from	material_atend_paciente a 
		where	a.nr_atendimento = nr_atendimento_p 
		and	coalesce(a.cd_motivo_exc_conta::text, '') = '' 
		
union
 
		SELECT	min(dt_procedimento) dt_item 
		from	procedimento_paciente b 
		where	b.nr_atendimento = nr_atendimento_p 
		and	coalesce(b.cd_motivo_Exc_conta::text, '') = '') alias7 
	where	1 = 1;
 
	return	dt_item_w;
 
else 
 
	return	dt_entrada_w;
 
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dt_primeiro_item_conta (nr_atendimento_p bigint) FROM PUBLIC;

