CREATE OR REPLACE FUNCTION obter_dt_prox_rendimento (nr_seq_aplicacao_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_prox_rend_w		timestamp;


BEGIN

if (nr_seq_aplicacao_p IS NOT NULL AND nr_seq_aplicacao_p::text <> '') then
	begin
	select 	CASE WHEN c.ie_forma_rend='D' THEN (coalesce(b.dt_rendimento,a.dt_aplicacao) + 1) WHEN c.ie_forma_rend='Q' THEN (case when (PKG_DATE_UTILS.extract_field('DAY',coalesce(b.dt_rendimento,a.dt_aplicacao)))::numeric 						between 1 and 15						then to_date('15/'||to_char(coalesce(b.dt_rendimento,a.dt_aplicacao),'mm/yyyy'))						else pkg_date_utils.end_of(coalesce(b.dt_rendimento,a.dt_aplicacao),'MONTH', 0)						end) WHEN c.ie_forma_rend='M' THEN pkg_date_utils.add_month(coalesce(b.dt_rendimento,a.dt_aplicacao), 1,0) WHEN c.ie_forma_rend='F' THEN (coalesce(b.dt_rendimento,a.dt_aplicacao) + c.nr_dias_rend) END  dt_rendimento
	into STRICT	dt_prox_rend_w
	FROM conta_banco_tipo_aplic c, banco_aplicacao a
LEFT OUTER JOIN banco_rendimento b ON (a.nr_sequencia = b.nr_seq_aplicacao)
WHERE c.nr_sequencia = a.nr_seq_aplicacao and coalesce(b.dt_rendimento,a.dt_aplicacao) = coalesce((SELECT max(x.dt_rendimento)
				from banco_rendimento x
				where x.nr_seq_aplicacao = a.nr_sequencia),a.dt_aplicacao) and a.nr_sequencia = nr_seq_aplicacao_p;
	exception when others then
	dt_prox_rend_w := null;
	end;
end if;

return dt_prox_rend_w;
end;
$body$
