CREATE OR REPLACE FUNCTION obter_dt_receb_solic ( nr_ordem_compra_p bigint, nr_solic_compra_p bigint, nr_item_oci_p bigint, nr_item_solic_compra_p bigint, ie_todas_notas_p text) RETURNS timestamp AS $body$
DECLARE


dt_recebimento_w timestamp;


BEGIN
	select 	max(dt_entrada)
	into STRICT dt_recebimento_w
	from (	SELECT	b.dt_entrada_saida dt_entrada
		from	nota_fiscal b,
			nota_fiscal_item a
		where	a.nr_sequencia       = b.nr_sequencia
		and	a.nr_ordem_compra    = nr_ordem_compra_p
		and	a.nr_item_oci        = nr_item_oci_p
		and	((ie_todas_notas_p = 'S') or
			(ie_todas_notas_p = 'N' AND b.ie_situacao = '1'))
		and	coalesce(a.nr_solic_compra::text, '') = ''
		
union

		SELECT	b.dt_entrada_saida dt_entrada
		from	nota_fiscal b,
			nota_fiscal_item a
		where	a.nr_sequencia          = b.nr_sequencia
		and	a.nr_solic_compra       = nr_solic_compra_p
		and	a.nr_item_solic_compra  = nr_item_solic_compra_p
		and	((ie_todas_notas_p = 'S')
		or	(ie_todas_notas_p = 'N' AND b.ie_situacao = '1'))
		and	(a.nr_solic_compra IS NOT NULL AND a.nr_solic_compra::text <> '')) alias9;




return	dt_recebimento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
