CREATE OR REPLACE FUNCTION obter_dt_receb_tit (nr_titulo_p bigint) RETURNS timestamp AS $body$
DECLARE

 
ds_retorno_w	timestamp;
nr_sequencia_w	titulo_receber_liq.nr_sequencia%type;
			
			 

BEGIN 
 
select	max(nr_sequencia) 
into STRICT	nr_sequencia_w 
from	titulo_receber_liq 
where	nr_titulo = nr_titulo_p;
 
if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then 
	select	max(dt_recebimento) 
	into STRICT	ds_retorno_w 
	from	titulo_receber_liq 
	where	nr_sequencia = nr_sequencia_w 
	and	coalesce(vl_descontos,0) <> 0;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
