CREATE OR REPLACE FUNCTION obter_dt_retorno_agenda_mult ( nr_seq_agenda_p bigint) RETURNS timestamp AS $body$
DECLARE


qt_media_w		bigint;


BEGIN

/* Pega a mÃ©dia do retorno */

begin
select	coalesce(avg(qt_dias_retorno),0)
into STRICT	qt_media_w
from	agenda_consulta_prof
where	nr_seq_agenda	= nr_seq_agenda_p
and	qt_dias_retorno	> 0;
