CREATE OR REPLACE FUNCTION obter_dt_setor_atendimento_pt ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, cd_setor_atendimento_p bigint, ie_prescr_nao_liberada_p text, nr_prescricao_p bigint, nr_prescricoes_p text, nr_prescr_titulo_p bigint, ie_setores_usuario_p text, ie_pac_usu_p text, nm_usuario_p text) RETURNS timestamp AS $body$
DECLARE

 
dt_setor_atendimento_w	timestamp;
nr_atendimento_w	bigint;
cd_setor_atendimento_w	integer;
ie_setor_atendimento_w	varchar(1);


BEGIN 
 
if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	begin 
	 
	nr_atendimento_w :=	obter_atendimento_pt(nr_atendimento_p,cd_pessoa_fisica_p,ie_prescr_nao_liberada_p,nr_prescricao_p,nr_prescricoes_p,nr_prescr_titulo_p,nm_usuario_p);
	 
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
	into STRICT	ie_setor_atendimento_w 
	from	setor_atendimento a 
	where	a.cd_setor_atendimento in ( 
		SELECT	b.cd_setor_atendimento 
		from	atend_paciente_unidade b 
		where	nr_atendimento 		= nr_atendimento_w 
		and	b.cd_setor_atendimento	= cd_setor_atendimento_p);
		 
	if (ie_pac_usu_p = 'U') and (ie_setores_usuario_p = 'S') then 
		begin 
		 
		cd_setor_atendimento_w := cd_setor_atendimento_p;
		end;
	else if (ie_pac_usu_p = 'U') and (nr_atendimento_w > 0) and (ie_setor_atendimento_w = 'S') then 
		begin 
		 
		cd_setor_atendimento_w := cd_setor_atendimento_p;
		end;
	else 
		begin 
		cd_setor_atendimento_w := coalesce(obter_unidade_atendimento(nr_atendimento_w,'IA','CS'),obter_unidade_atendimento(nr_atendimento_w,'A','CS'));
		end;
	end if;
	end if;
	 
	select	to_char(clock_timestamp(), 'dd/MM/yyyy') || ' ' || to_char(hr_inicio_prescricao, 'hh24:mi:ss') 
	into STRICT	dt_setor_atendimento_w 
	from	setor_atendimento 
	where	cd_setor_atendimento = cd_setor_atendimento_w;
	 
	end;
end if;
return	dt_setor_atendimento_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
