CREATE OR REPLACE FUNCTION obter_dt_termino_per_agecons ( nr_seq_agenda_p bigint, ie_diario_p text, ie_final_semana_p text, qt_dias_p bigint, ds_dias_p text) RETURNS varchar AS $body$
DECLARE


qt_dia_w		smallint;
dt_agenda_w	timestamp;
dt_atual_w	timestamp;
cd_dia_semana_w	varchar(1);
ds_retorno_w	varchar(255);
qt_dias_w	bigint := 0;
ds_dias_w	varchar(255);
ie_gerar_dia_w	varchar(1)  := 'S';


BEGIN

if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then
	begin
	/* obter dados agenda */

	select	max(dt_agenda)
	into STRICT	dt_agenda_w
	from	agenda_consulta
	where	nr_sequencia = nr_seq_agenda_p;

	/* Gerar hor√°rios por semana ou diariamente */

	if (ie_diario_p = 'S') then
		qt_dia_w	:= 1;
	elsif (ie_diario_p = 'N') then
		qt_dia_w	:= 7;
	end if;

	dt_atual_w	:= dt_agenda_w + 1;

	if (ds_dias_p IS NOT NULL AND ds_dias_p::text <> '') then
		ds_dias_w := replace(ds_dias_p, ' ', '');
	end if;


	if (qt_dias_p > 0) then
		while(qt_dias_w < qt_dias_p) loop
			begin

			select	substr(Obter_Cod_Dia_Semana(dt_atual_w),1,1)
			into STRICT	cd_dia_semana_w
			;

			if (ie_final_semana_p = 'N') and
				((cd_dia_semana_w = 1) or (cd_dia_semana_w = 7))then

				if (cd_dia_semana_w = 1) then
					dt_atual_w	:= dt_atual_w + 1;
				elsif (cd_dia_semana_w = 7) then
					dt_atual_w	:= dt_atual_w + 2;
				end if;


				select	substr(Obter_Cod_Dia_Semana(dt_atual_w),1,1)
				into STRICT	cd_dia_semana_w
				;
			end if;

			if (ds_dias_w IS NOT NULL AND ds_dias_w::text <> '') then
				ie_gerar_dia_w	:=  obter_se_contido(cd_dia_semana_w,ds_dias_w);

				if (ie_gerar_dia_w	= 'S') then
					qt_dias_w	:= qt_dias_w + 1;
				end if;
			else
				qt_dias_w	:= qt_dias_w + 1;
			end if;

			dt_atual_w	:= dt_atual_w + 1;
			end;
		end loop;
	end if;

	ds_retorno_w	:= dt_atual_w - 1;
	end;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
