CREATE OR REPLACE FUNCTION obter_dt_ultima_autor_int (nr_atendimento_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_retorno_w	timestamp;


BEGIN

--Primeiro verifica se tem alguma prorrogação que foi autorizado por último.
select	max(a.dt_fim_vigencia)
into STRICT	dt_retorno_w
from	autorizacao_convenio a,
	estagio_autorizacao b
where	a.nr_seq_estagio	= b.nr_sequencia
and	a.nr_atendimento	= nr_atendimento_p
and	b.ie_interno		= 10 --Somente se autorizado
and	a.ie_tipo_autorizacao	= '2';

--Se não encontrou, verifica a internação.
if (coalesce(dt_retorno_w::text, '') = '') then
	select	max(a.dt_fim_vigencia)
	into STRICT	dt_retorno_w
	from	autorizacao_convenio a,
		estagio_autorizacao b
	where	a.nr_seq_estagio	= b.nr_sequencia
	and	a.nr_atendimento	= nr_atendimento_p
	and	b.ie_interno		= 10 --Somente se autorizado
	and	a.ie_tipo_autorizacao	= '1';
end if;

return dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
