CREATE OR REPLACE FUNCTION obter_dt_ult_alteracao_dieta ( nr_seq_mapa_p bigint) RETURNS varchar AS $body$
DECLARE


dt_alteracao_w		varchar(100);
cd_pessoa_fisica_w	varchar(10);
ie_destino_dieta_w	varchar(2);
nr_atendimento_w	bigint;


BEGIN

if (nr_seq_mapa_p IS NOT NULL AND nr_seq_mapa_p::text <> '') then

	select	cd_pessoa_fisica,
		ie_destino_dieta,
		nr_atendimento
	into STRICT	cd_pessoa_fisica_w,
		ie_destino_dieta_w,
		nr_atendimento_w
	from	mapa_dieta
	where	nr_sequencia = nr_seq_mapa_p;

	select	to_char(max(dt_alteracao),'dd/mm/yyyy hh24:mi:ss')
	into STRICT	dt_alteracao_w
	from	md_alterar_dieta a,
		mapa_dieta b
	where	a.nr_seq_mapa = b.nr_sequencia
	and	b.cd_pessoa_fisica = cd_pessoa_fisica_w
	and	b.ie_destino_dieta = ie_destino_dieta_w
	and	b.nr_atendimento = nr_atendimento_w;
end if;

return	dt_alteracao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
