CREATE OR REPLACE FUNCTION obter_dt_ult_fecha_atend (nr_atendimento_p bigint) RETURNS timestamp AS $body$
DECLARE

 
dt_ultimo_fech_w	timestamp;


BEGIN 
 
select	max(a.dt_periodo_final) 
into STRICT	dt_ultimo_fech_w 
from	conta_paciente a 
where	(a.nr_seq_apresentacao IS NOT NULL AND a.nr_seq_apresentacao::text <> '') 
and	a.nr_atendimento = nr_atendimento_p 
and	a.ie_status_acerto = 2 
and	a.nr_seq_apresentacao =	(SELECT	max(b.nr_seq_apresentacao) 
				from	conta_paciente b 
				where	b.nr_atendimento = a.nr_atendimento 
				and	(b.nr_seq_apresentacao IS NOT NULL AND b.nr_seq_apresentacao::text <> '') 
				and	a.ie_status_acerto = 2);
 
return	dt_ultimo_fech_w;
 
end;
