CREATE OR REPLACE FUNCTION obter_dt_utm_acesso_tasy ( nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(20);


BEGIN
select	to_char(a.dt_acesso,'dd/mm/yyyy hh24:mi:ss')
into STRICT	ds_retorno_w
from	tasy_log_acesso a
where	a.nr_sequencia = (	SELECT	max(x.nr_sequencia)
			from	tasy_log_acesso x
			where	x.nm_usuario = nm_usuario_p
			and	x.ie_result_acesso = 'N'
			and	dt_acesso > clock_timestamp() - interval '60 days');

return	ds_retorno_w;

end;
