CREATE OR REPLACE FUNCTION obter_dt_vencimento_lote_reaju (nr_seq_quota_parc_p pls_escrit_quota_parcela.nr_sequencia%type) RETURNS timestamp AS $body$
DECLARE


dt_vencimento_w 	pls_escrit_quota_parcela.dt_vencimento%type;


BEGIN

if (nr_seq_quota_parc_p IS NOT NULL AND nr_seq_quota_parc_p::text <> '') then
	select	dt_vencimento
	into STRICT	dt_vencimento_w
	from	pls_escrit_quota_parcela
	where 	nr_sequencia		= nr_seq_quota_parc_p;
end if;

return	trunc(dt_vencimento_w);

end;
