CREATE OR REPLACE FUNCTION obter_dt_venc_cheque_cr (nr_seq_cheque_p bigint, dt_referencia_p timestamp) RETURNS timestamp AS $body$
DECLARE


dt_vencimento_atual_w	timestamp;

c01 CURSOR FOR
SELECT	dt_vencimento_atual
from	cheque_cr_alt_venc
where	nr_seq_cheque				= nr_seq_cheque_p
and	trunc(dt_atualizacao_nrec, 'DD')	<= trunc(dt_referencia_p, 'DD')
order 	by dt_atualizacao_nrec;


BEGIN

dt_vencimento_atual_w		:= null;
open c01;
loop
fetch c01 into
	dt_vencimento_atual_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
end loop;
close c01;

if (coalesce(dt_vencimento_atual_w::text, '') = '') then
	select	coalesce(dt_vencimento_atual, dt_vencimento)
	into STRICT	dt_vencimento_atual_w
	from	cheque_cr
	where	nr_seq_cheque	= nr_seq_cheque_p;
end if;

return	dt_vencimento_atual_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
