CREATE OR REPLACE FUNCTION obter_dt_venc_protocolo (nr_interno_conta_p bigint, nr_seq_protocolo_p bigint) RETURNS timestamp AS $body$
DECLARE

 
dt_vencimento_w	timestamp := null;


BEGIN 
 
if (nr_seq_protocolo_p IS NOT NULL AND nr_seq_protocolo_p::text <> '') then 
	select	max(dt_vencimento) 
	into STRICT	dt_vencimento_w 
	from	protocolo_convenio 
	where	nr_seq_protocolo	= nr_seq_protocolo_p;
elsif (nr_interno_conta_p IS NOT NULL AND nr_interno_conta_p::text <> '') then 
	select	max(b.dt_vencimento) 
	into STRICT	dt_vencimento_w 
	from	protocolo_convenio b, 
		conta_paciente a 
	where	b.nr_seq_protocolo	= a.nr_seq_protocolo 
	and	a.nr_interno_conta	= nr_interno_conta_p;
end if;
 
return	dt_vencimento_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
